import React from 'react';
import Header from './components/layout/Header';
import Form from './components/InputForm'
import Table from "./components/Table"
import { BrowserRouter as Router, Route } from 'react-router-dom'
import './App.css';

import { Provider } from "react-redux"
import store from "./store" 

store.subscribe(()=>console.log(store.getState()))

class App extends React.Component {
  constructor(props){
    super(props)
    this.state={
      // form data is an array containing all the information entered in the form
      formData:[]
    }
  }

  // create a custom method to add information to the state 
  validateFields=(fields)=>{

  }

  render(){
    return (
      // wrap the app in provider to make store globally accesible
      <Provider store={store}>

        {/* use a router to selectively display content */}
        <Router>
          <div className="App">
          {/* header component */}
            <Header />

            {/* route for the home page which contains the form */}
            <Route exact path="/" render={props=>(
                  <React.Fragment>
                    <div className="content">
                      <div className="formWrapper">
                        <Form customValidator={this.validateFields}/>
                      </div>
                    </div>
                  </React.Fragment>
                  )}>
            </Route>


            {/* route for the page which contains the table */}
            <Route exact path="/table" render={props=>(
                  <React.Fragment>
                    <Table className="table" tableData={this.state.formData}/>
                  </React.Fragment>
                  )}>
            </Route>

          </div>
        </Router>

      </Provider>
    );
  }
}

export default App;
