import React from 'react';
import Header from './components/layout/Header';
import Form from './components/InputForm'
import Table from "./components/Table"
import uuid from 'react-uuid'
import { BrowserRouter as Router, Route } from 'react-router-dom'
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state={
      // form data is an array containing all the information entered in the form
      formData:[]
    }
  }

  // create a custom method to add information to the state 
  validateFields=(fields)=>{

    const {formData}=this.state
    // get the formdata from the fields
    const {firstName,lastName,age,hobbies}=fields
    // convert the data from the date picker to a readable format
    const dateOfBirth=fields['date-picker'].format("DD-YY-MM")
    // create a unique id for this object
    const key=uuid()
    // create an object containing these items
    const detail = {
      key,
      firstName,
      lastName,
      age,
      dateOfBirth,
      hobbies
    }
    // append the newly gotten data to the table state
    this.setState({formData:[...formData,detail]})
  }

  render(){
    return (
      <div className="App">
        <Header />
        <Route exact path="/" render={props=>(
              <React.Fragment>
                <div className="content">
                  <div className="formWrapper">
                    <Form customValidator={this.validateFields}/>
                  </div>
                </div>
              </React.Fragment>
              )}>
        </Route>

        <Route exact path="/about" render={props=>(
              <React.Fragment>
                <div className="content">
                  <div className="formWrapper">
                    <Form customValidator={this.validateFields}/>
                  </div>
                </div>
              </React.Fragment>
              )}>
        </Route>

        <Route path="/table" >
          <Table tableData={this.state.formData}/>
        </Route>

      </div>
    );
  }
}

export default App;
